package com.pratchaya.ws.rs.service;

import com.pratchaya.util.ImageCV;
import com.pratchaya.util.ImageUtils;
import com.pratchaya.xml.ItemResourceInterface;
import com.pratchaya.xml.ItemCreate;
import java.awt.image.BufferedImage;
import java.util.ArrayList;
import java.util.LinkedHashMap;
import java.util.LinkedList;
import java.util.List;
import java.util.Map;
import javax.ws.rs.Consumes;
import javax.ws.rs.GET;
import javax.ws.rs.POST;
import javax.ws.rs.Path;
import javax.ws.rs.Produces;
import javax.ws.rs.core.MediaType;
import javax.ws.rs.core.Response;
import org.codehaus.jettison.json.*;

/**
 * REST Web Service
 *
 * @author suputsopon
 */
@Path("json/service")
public class SeviceResource implements ItemResourceInterface {

    private String image = "";
    //resources/json/service

    @GET
    @Path("/api")
    @Produces(MediaType.APPLICATION_JSON)
    public Response doGet() {
        List<ItemCreate> items = new ArrayList<ItemCreate>();
        items.add(new ItemCreate("8", "3", "5", "data:image/png;base64,", "data:image/png;base64,"));
        return Response.status(200).entity(items).build();

    }

    @POST
    @Path("/api")
    @Consumes({MediaType.APPLICATION_JSON})
    @Produces({MediaType.APPLICATION_JSON})
    @Override
    public Response doPost(String data) {

        try {


            BufferedImage bt = null;

            JSONObject json = new JSONObject(data);
            List<String> l = new ArrayList<String>();
            JSONArray arr = json.getJSONArray("Image");
            l.add(arr.getJSONObject(0).getString("image"));
            bt = ImageUtils.decodeToImage(l.get(0));
            bt = ImageCV.convert(bt);
            //

            //IplImage i = IplImage.createFrom(bt);



            String s = ImageUtils.encodeToString(bt, "jpeg");

            Map map = new LinkedHashMap();
            List list = new LinkedList();
            map.put("width", bt.getWidth()+60);
            map.put("height", bt.getHeight());
            map.put("color_mode", bt.getColorModel().toString());
            map.put("image", "data:image/jpeg;base64," + s);
            list.add(map);

            return Response.status(200).entity(list).build();
        } catch (Exception ex) {
            //Logger.getLogger(SeviceResource.class.getName()).log(Level.SEVERE, null, ex);
            return Response.status(Response.Status.NO_CONTENT).entity("JSON NULL").build();
        }
    }

    /**
     * @return the image
     */
    public String getImage() {
        return image;
    }

    /**
     * @param image the image to set
     */
    public void setImage(String image) {
        this.image = image;
    }
}
